const targets=document.querySelectorAll("img");var lazyLoad=function(t){new IntersectionObserver(function(t,e){t.forEach(function(t){if(t.isIntersecting){var s=t.target;(s.classList.contains("details-background-img")||s.classList.contains("phone-img"))&&s.classList.add("fade-in");const a=s.getAttribute("data-lazy");s.setAttribute("src",a),e.disconnect()}})}).observe(t)};targets.forEach(lazyLoad);